sub oneOf($@)                                                                   # Check the first number is in the remaining numbers
 {my ($a, @a) = @_;
  for(@a)
   {return 1 if $a == $_;
   }
  undef
 }

sub oneOfStrings($@)                                                            # Check the first string is in the remaining numbers
 {my ($a, @a) = @_;
  Confess "string required" unless defined $a;
  for(@a)
   {return 1 if $a eq $_;
