sub readFile($)
 {my ($f) = @_;
  $f or ::Confess "readFile: No file specified";
  open my $F, "<$f" or ::Confess "Cannot open $f for input";
  local $/ = undef;
  <$F>;
 }

sub readUnicode($)
 {my ($f) = @_;
  open(my $F, "<:encoding(UTF-8)", $f) or ::Confess "Cannot open $f for unicode input";
  local $/ = undef;
  <$F>;
 }

