  Log $text;
  for my $app(@app)
   {next unless $app->active and !$app->ignore;
    for my $subset(0..$app->subsets)
     {next if $app->skipSubset($subset, $genAndroid);
      $sub->($app, $subset, $genAndroid);
     }
   }
 }

