  sub keywords($)                                                               # Keywords by calculation
   {my ($app) = @_;
    my $g = $app->group;
    my %oneLiners = %{&::oneLiners};
    my %c;
    for(values %oneLiners)
     {$c{$_}++ for split /\s+/, s/[^a-z]/ /gir;
