    my $I = &::amazonKeysToIds;
    my $K = $app->{amazon};
    ref($K) =~ /array/i or ::Confess "Wong number of Amazon keys vs subsets for app $g";
    if (my $k = $K->[$subset])
     {if (my $i = $I->{$k}{id})
       {return $i;
