  my $N = 0;
  my $s = '';

  for(@T)
   {my @t = grep {!/\A\s*\Z/} split(/\n/, $_);
    my $name = shift @t; shift @t; unshift @t, ($name, $translateText1);
    my @lines;
    for(0..$#t)
     {my $nn = 'a'; my $n = sub {$nn++};
      $N++;
      my $label = lc(join('', map {(&$n, $_)} split(//, $N)));
      my $text  = $t[$_];

      $s .= "($label)  $text\n";
      push @lines, [$N, $label, $text];
     }
    $D->{apps}{$name} = [@lines];
   }
  if ($writeFile)
   {my $f = "unicode/text2Translate.data";
