import java.util.TreeMap;

public class Assets
 {final TreeMap<String,Boolean> assets = new TreeMap<String,Boolean>();
  void load(String S)
   {for(String s: S.split(":")) assets.put(s, true);
   }
  public Assets() {
END
  for(1..@s)
   {my $t = $s[$_-1]; chop($t);
    $s .= '    load("'.$t.'");'."\n";
   }
  $s .= <<END;
   }
