sub sendAppaApps($$)                                                            # Send to www.appa apps.com
 {my ($source, $gen) = @_;                                                      # Apk file, Generate request
  my (     $apk,      $subset,      $genAndroid) =
     ($gen->apk, $gen->subset, $gen->genAndroid);
