 {package ColourApplication;
  use base qw(Application);

  sub vividColour()
   {my ($C, $L, $H) = (int(rand(256)), int(rand(64)), 192+int(rand(64)));
    my @c = ($H, $L, $C);
    my $s = int(rand(6));

    @c = ($C, $L, $H) if $s == 0;
    @c = ($C, $H, $L) if $s == 1;
    @c = ($L, $C, $H) if $s == 2;
    @c = ($H, $C, $L) if $s == 3;
    @c = ($L, $H, $C) if $s == 4;
    sprintf("%02x%02x%02x", @c);
   }

