      my $name   = $_->name;
      my $title  = $_->title;
      my $colour = $_->colour;
      my $prefix = $_->prefix;

      $s .= <<"END" if $title eq $name;
`  add($prefix, 0x$colour, "$title");
END
      $s .= <<"END" if $title ne $name;
`  add($prefix, 0x$colour, "$name", "$title");
END
     }
    $s
   }
 }

