  my %h;
  sub add($$)
   {my ($name, $sequence) = @_;
    $h{$name} = {group=>1, name=>$name, sequence=>$sequence, order=>$name};
   }

  sub genJava($$)
   {my ($data, $app) = @_;
    my $s = '';
    for($app->things)
     {my $group = $_->group;
      my $name  = $_->name;
      my $title = $_->title;
      my $sequence = $_->sequence;

      $s .= <<"END" if $name eq $title;
`  Add($sequence, "$name");
END
      $s .= <<"END" unless $name eq $title;
`  Add($sequence, "$name", "$title");
END
     }
    $s
   }

