 {package TheNumbersFrom1To100InEnglish;
  use base qw(ColourApplication);

  sub add($$$)
   {my ($h, $name, $title) = @_;
    $h->{$name} = {colour=>-1, name=>$name, prefix=>$name+0, title=>$title, order=>sprintf("%04d", $name)};
   }

  sub genJava($$)
   {my ($data, $app) = @_;
    my $s = '';
    for(sort {$a->{order} <=> $b->{order}} $app->things)
     {my $name   = $_->name;
      my $prefix = $_->prefix;
      my $title  = $_->title;

      $s .= <<"END";
`  Add($prefix, "$name", "$title");
END
     }
    $s
   }

  sub genFacts()
   {my $f;

# Plus
    for   my $i(1..10)
     {for my $j($i..10)
       {push @{$f->{$i+$j}}, "$i + $j";
       }
     }

# Times
    for   my $i(2..10)
     {for my $j($i..10)
       {push @{$f->{$i*$j}}, "$i * $j";
       }
     }
# Results
    for(sort {$a <=> $b} keys %$f)
     {my ($k, $v) = ($_, $f->{$_});
      say '';
      ::Log "$k = $_" for @$v
      }
    }

