  sub genJava($$)
   {my ($data, $app) = @_;
    my $s = '';
    for($app->things)
     {my $group  = $_->group;
      my $name   = $_->name;
      my $colour = $_->colour;
      my $prefix = $_->prefix;

      $s .= <<"END";
`  add($group, $prefix, 0x$colour, "$name");
